import React, { useState } from 'react';
import styled from 'styled-components';

const SignupContainer = styled.div`
  width: 100%;
  background: #2c3e50;
  height: 400px;
  padding: 20px;
`;

const Input = styled.input`
  margin-top: 20px;
  margin-left: 105px;
  height: 25px;
  border: none;
  border-radius: 2px;
  &.first,
  &.last,
  &.confirm {
    width: 250px;
  }
  &.email {
    width: 175px;
  }
  &.password {
    width: 125px;
    margin-left: 6px !important;
  }
`;

const ButtonSubmit = styled.button`
  margin-top: 20px;
  margin-left: 105px;
  height: 25px;
  border: none;
  border-radius: 2px;
`;

const ErrorText = styled.p`
  color: red;
  margin-left: 105px;
  margin-top: 5px;
  font-size: 12px;
`;

const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validate = () => {
    let errors = {};
    if (!formData.firstName) errors.firstName = 'First Name is required';
    if (!formData.lastName) errors.lastName = 'Last Name is required';
    if (!formData.email) errors.email = 'Email is required';
    if (!/\S+@\S+\.\S+/.test(formData.email)) errors.email = 'Email is invalid';
    if (!formData.password) errors.password = 'Password is required';
    if (formData.password.length < 6) errors.password = 'Password must be at least 6 characters';
    if (formData.password !== formData.confirmPassword) errors.confirmPassword = 'Passwords do not match';
    return errors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length === 0) {
      // Submit form to the server
      console.log('Form submitted', formData);
    } else {
      setErrors(validationErrors);
    }
  };

  return (
    <SignupContainer>
      <form onSubmit={handleSubmit}>
        <Input type="text" name="firstName" className="first" placeholder="First Name" value={formData.firstName} onChange={handleChange} />
        {errors.firstName && <ErrorText>{errors.firstName}</ErrorText>}
        <Input type="text" name="lastName" className="last" placeholder="Last Name" value={formData.lastName} onChange={handleChange} />
        {errors.lastName && <ErrorText>{errors.lastName}</ErrorText>}
        <Input type="email" name="email" className="email" placeholder="Email" value={formData.email} onChange={handleChange} />
        {errors.email && <ErrorText>{errors.email}</ErrorText>}
        <Input type="password" name="password" className="password" placeholder="Password" value={formData.password} onChange={handleChange} />
        {errors.password && <ErrorText>{errors.password}</ErrorText>}
        <Input type="password" name="confirmPassword" className="confirm" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} />
        {errors.confirmPassword && <ErrorText>{errors.confirmPassword}</ErrorText>}
        <ButtonSubmit type="submit">Send</ButtonSubmit>
      </form>
    </SignupContainer>
  );
};

export default Signup;
