import React, { useState } from 'react';
import styled from 'styled-components';
import Login from '../Login';
import Signup from '../SignUp';


const Body = styled.div`
  width: 100%;
  height: 700px;
  margin: 0;
  padding: 0;
`;

const Space = styled.div`
  width: 500px;
  margin: auto;
  margin-top: 10%;
  font-family: 'Roboto', sans-serif;
`;

const Buttons = styled.div`
  height: 100px;
  background: #2c3e50;
  display: flex;
  justify-content: space-between;
`;

const Button = styled.p`
  width: 49.95%;
  color: white;
  text-align: center;
  padding-top: 25px;
  height: 75%;
  margin: 0;
  cursor: pointer;
  &:hover {
    background: #f1c40f;
    color: #2c3e50;
  }
  &.yellow {
    background-color: gold;
  }
`;

const LoginRegister = () => {
  const [state, setState] = useState({ signup: false, login: true });

  const switchForm = (word) => {
    if (word === 'signup') {
      setState({ signup: true, login: false });
    } else {
      setState({ signup: false, login: true });
    }
  };

  return (
    <Body>
      <Space>
        <Buttons>
          <Button onClick={() => switchForm('signup')} className={state.signup ? 'yellow' : ''}>
            Sign In
          </Button>
          <Button onClick={() => switchForm('login')} className={state.login ? 'yellow' : ''}>
            Login
          </Button>
        </Buttons>

        {state.signup ? <Signup/> : null}
        {state.login ? <Login/> : null}
      </Space>
    </Body>
  );
};

export default LoginRegister;
