import React, { useState, useEffect, useRef } from "react";
import { Input, Button, Typography, Card } from "antd";
import { SendOutlined } from "@ant-design/icons";
import styled from "styled-components";
import axios from "axios";
import Cookies from 'js-cookie'
import { Navigate } from "react-router";

const { TextArea } = Input;
const { Text } = Typography;

const predefinedOptions = [
  {
    question: "What is aptitude?",
    answer: "Aptitude is a natural ability to do something well.",
  },
  {
    question: "How can I improve my teaching skills?",
    answer: "Practice regularly and seek feedback from peers and students.",
  },
  {
    question: "What resources can help with teaching?",
    answer:
      "There are many online courses, books, and workshops available to help improve teaching skills.",
  },
];

const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  max-height: 100vh;
  padding: 16px;
  background-color: #f0f2f5;
`;

const ChatBox = styled(Card)`
  flex: 1;
  overflow-y: auto;
  margin-bottom: 16px;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
  display: flex;
  flex-direction: column;
`;

const OptionsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 16px;
`;

const OptionButton = styled(Button)`
  flex: 1 1 auto;
  margin: 4px 0;
`;

const MessageContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 16px;
`;

const BotMessage = styled.div`
  padding: 12px 20px;
  background: #e6f7ff;
  border: 1px solid #d9d9d9;
  border-radius: 8px;
  margin-bottom: 8px;
  align-self: flex-start;
  max-width: 80%;
  text-align: left;
`;

const UserMessage = styled.div`
  padding: 12px 20px;
  background: #fff;
  border: 1px solid #d9d9d9;
  border-radius: 8px;
  margin-bottom: 8px;
  align-self: flex-end;
  max-width: 80%;
  text-align: right;
`;

const InputContainer = styled.div`
  display: flex;
  gap: 8px;
`;

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleOptionClick = (option) => {
    setMessages([
      ...messages,
      { text: option.question, isBot: false },
      { text: option.answer, isBot: true },
    ]);
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSend = async () => {
    if (inputValue.trim() === "") return;
    const userMessage = { text: inputValue, isBot: false };
    setMessages([...messages, userMessage]);
    setInputValue("");

    try {
      await axios.post("http://localhost:8000/ticket", {
        userId: "USER_ID",
        message: inputValue,
      });
      await axios.post("http://localhost:8000/conversation", {
        userId: "USER_ID",
        messages: [...messages, userMessage],
      });
      setMessages((prev) => [
        ...prev,
        {
          text: "Your ticket has been raised and we will get back to you soon.",
          isBot: true,
        },
      ]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  const token = Cookies.get("jwt_token");
  
  if (!token) {
    return <Navigate to="/" />;
  }

  return (
    <ChatContainer>
      <ChatBox title="Chatbot">
        <MessageContainer>
          {messages.map((item, index) =>
            item.isBot ? (
              <BotMessage key={index}>
                <Text>{item.text}</Text>
              </BotMessage>
            ) : (
              <UserMessage key={index}>
                <Text>{item.text}</Text>
              </UserMessage>
            )
          )}
          <div ref={chatEndRef} />
        </MessageContainer>
      </ChatBox>
      <OptionsContainer>
        {predefinedOptions.map((option, index) => (
          <OptionButton key={index} onClick={() => handleOptionClick(option)}>
            {option.question}
          </OptionButton>
        ))}
      </OptionsContainer>
      <InputContainer>
        <TextArea
          value={inputValue}
          onChange={handleInputChange}
          maxLength={250}
          placeholder="Enter your message here..."
          autoSize={{ minRows: 1, maxRows: 3 }}
        />
        <Button type="primary" icon={<SendOutlined />} onClick={handleSend}>
          Send
        </Button>
      </InputContainer>
    </ChatContainer>
  );
};

export default Chatbot;
