// src/Chatbot.js
import React, { useState, useEffect, useRef } from 'react';
import { Input, Button, List, Card, Typography } from 'antd';
import { SendOutlined } from '@ant-design/icons';
import styled from 'styled-components';

const { TextArea } = Input;
const { Text } = Typography;

const predefinedOptions = [
  { question: "What is aptitude?", answer: "Aptitude is a natural ability to do something well." },
  { question: "How can I improve my teaching skills?", answer: "Practice regularly and seek feedback from peers and students." },
  { question: "What resources can help with teaching?", answer: "There are many online courses, books, and workshops available to help improve teaching skills." },
];

const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  max-height: 100vh;
  padding: 16px;
  background-color: #f0f2f5;
`;

const ChatBox = styled.div`
  flex: 1;
  overflow-y: auto;
  margin-bottom: 16px;
  background: #fff;
  padding: 16px;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
`;

const OptionsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 16px;
`;

const OptionButton = styled(Button)`
  flex: 1 1 auto;
  margin: 4px 0;
`;

const Message = styled.div`
  padding: 8px;
  background: ${props => (props.isBot ? '#e6f7ff' : '#fff')};
  border: 1px solid #d9d9d9;
  border-radius: 8px;
  margin-bottom: 8px;
  align-self: ${props => (props.isBot ? 'flex-start' : 'flex-end')};
  max-width: 80%;
`;

const InputContainer = styled.div`
  display: flex;
  gap: 8px;
`;

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [showOptions, setShowOptions] = useState(true);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleOptionClick = (option) => {
    setMessages([...messages, { text: option.question, isBot: false }, { text: option.answer, isBot: true }]);
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSend = () => {
    if (inputValue.trim() === '') return;
    setMessages([...messages, { text: inputValue, isBot: false }]);
    setInputValue('');
    setShowOptions(false);
    // Simulate server response
    setTimeout(() => {
      setMessages(prev => [...prev, { text: "Your ticket has been raised and we will get back to you soon.", isBot: true }]);
    }, 1000);
  };

  return (
    <ChatContainer>
      <ChatBox>
        <List
          dataSource={messages}
          renderItem={(item) => (
            <Message isBot={item.isBot}>
              <Text>{item.text}</Text>
            </Message>
          )}
        />
        <div ref={chatEndRef} />
      </ChatBox>
      {showOptions && (
        <OptionsContainer>
          {predefinedOptions.map((option, index) => (
            <OptionButton key={index} onClick={() => handleOptionClick(option)}>
              {option.question}
            </OptionButton>
          ))}
        </OptionsContainer>
      )}
      <InputContainer>
        <TextArea
          value={inputValue}
          onChange={handleInputChange}
          maxLength={250}
          placeholder="Enter your message here..."
          autoSize={{ minRows: 1, maxRows: 3 }}
        />
        <Button type="primary" icon={<SendOutlined />} onClick={handleSend}>
          Send
        </Button>
      </InputContainer>
    </ChatContainer>
  );
};

export default Chatbot;
